export const getCities = (cities) => {return {type: 'GET_CITIES',cities : cities} }



 export const getAllCities= (dispatch) => {
    return fetch('/api/cities')
    .then(response => {
       response.json();
    })
    .then(json => dispatch(getCities(json)))
    .catch(error => {
      console.log(error);
    });
  }
  export const fetchCitiesData = () => {
    console.log('action')
    return (dispatch) => {
        fetch("/api/city", {
            method: "GET",
            mode: "no-cors",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/x-www-form-urlencoded'
            },
        })
        .then(r => r.json())
        .then(json => {
            dispatch(getCitiesData(json))
        })
        .catch(e => console.log(e))
    }
}

  export function loadCities() {  
    return function(dispatch) {
      return getAllCities().then(cities => {
        dispatch(getCities(cities));
      }).catch(error => {
        throw(error);
      });
    };
  }
